import { GoogleMap, Marker, useLoadScript } from "@react-google-maps/api";
import { useMemo } from "react";
import styles from "./MapOfGoogle.module.scss";

const MapOfGoogle = () => {
  const [places, setPlaces] = useState([]);

  const searchNearbyPlaces = async (location) => {
    try {
      const response = await fetch(
        `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${location.lat},${location.lng}&radius=5000&type=tourist_attraction&key=AIzaSyDpSrQ5Ey93gZgzhAWp0AlGAa351IUkUOI`
      );
      const data = await response.json();
      setPlaces(data.results);
    } catch (error) {
      console.error("Error searching for places:", error);
    }
  };

  const { isLoaded } = useLoadScript({
    googleMapsApiKey: "AIzaSyDpSrQ5Ey93gZgzhAWp0AlGAa351IUkUOI",
  });

