import React, { useContext } from "react";
import { useForm } from "react-hook-form";
import * as Yup from "yup";
import { AuthContext } from "../../context/AuthContext";
import { yupResolver } from "@hookform/resolvers/yup";
import { Navigate, useNavigate } from "react-router-dom";
import { GoogleLogin } from "@react-oauth/google";
import jwt_decode from "jwt-decode";

export default function SignIn() {
  const { signin, user } = useContext(AuthContext);
  const navigate = useNavigate();

  const validationSchema = Yup.object().shape({
    email: Yup.string()
      .email("Email non valide")
      .required("Ce champ doit être saisi"),
    password: Yup.string()
      .required("Required")
      .min(6, "Le mot de passe doit contenir 6 caractères min."),
  });

  const initialValues = {
    email: "",
    password: "",
  };

  const {
    handleSubmit,
    register,
    formState: { errors, isSubmitting },
    setError,
    clearErrors,
  } = useForm({
    initialValues,
    resolver: yupResolver(validationSchema),
  });

  const submit = handleSubmit(async (values) => {
    try {
      clearErrors();
      console.log(values);
      await signin(values);
      navigate("/");
    } catch (message) {
      setError("generic", { type: "generic", message });
    }
  });

  return (
    <>
      {user ? (
        <Navigate to="/profile" />
      ) : (
        <div className="d-flex justify-content-center">
          <div className={` m30`}>
            <h1 className="text-center">Connexion</h1>
            <form
              onSubmit={submit}
              className="d-flex flex-column justify-content-center align-items-center p20"
            >
              <div className="d-flex flex-column">
                <label htmlFor="email" className="mb10 pl20">
                  Email:
                </label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  {...register("email")}
                />
                {errors?.email && (
                  <p className="form-error">{errors.email.message}</p>
                )}
              </div>

              <div className="d-flex flex-column mt10 mb20">
                <label htmlFor="password" className="m-2">
                  Mot de passe :
                </label>
                <input
                  type="password"
                  className="form-control"
                  name="password"
                  {...register("password")}
                />
                {errors.password && (
                  <p className="form-error">{errors.password.message}</p>
                )}
              </div>
              {errors.generic && (
                <p className="form-error">{errors.generic.message}</p>
              )}
              <div className="mt-2">
                <button disabled={isSubmitting} className="btn btn-primary">
                  Se connecter
                </button>
              </div>
            </form>

            <div className="d-flex flex-column">


              <GoogleLogin
                onSuccess={(credentialResponse) => {
                  console.log(credentialResponse);
                  let decoded = jwt_decode(credentialResponse.credential);
                  console.log(decoded);
                  console.log(decoded.family_name);
                }}
                onError={() => {
                  console.log("Login Failed");
                }}
                useOneTap
              />
            </div>
          </div>
        </div>
      )}
    </>
  );
}
