import React, { useContext } from "react";
import styles from "./Header.module.scss";
import { useState } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../../context";
import logo from "../../assets/png/logo.png";
import InfoUser from "../../pages/homepage/componants/InfoUser";

export default function Header() {
  const {
    userGoogle,
    user,
    signout,
    signoutGoogle,
    userFacebook,
    signoutFacebook,
  } = useContext(AuthContext);
  const [showMenu, setShowMenu] = useState(false);
  const [count, setCount] = useState(0);

  let userPictureUrl = "";

  if (Object.keys(userGoogle).length !== 0) {
    // Utilisateur connecté avec Google
    userPictureUrl = userGoogle.picture;
  } else if (Object.keys(userFacebook).length !== 0) {
    // Utilisateur connecté avec Facebook
    userPictureUrl = userFacebook.picture.data.url;
  }

  return (
    <header className={`${styles.header} container-fluid`}>
      <div className="row">
        <div className="col-4">
          {" "}
          <InfoUser />
        </div>
        <div className="col-4 m-auto text-center">
          <img src={logo} alt="logo 20 sorties" className="logo "></img>
        </div>
        <div className="d-flex justify-content-end col-4">
          <ul className={`${styles.desktopHeader}`}>
            {user ||
            (userGoogle && Object.keys(userGoogle).length !== 0) ||
            (userFacebook && Object.keys(userFacebook).length !== 0) ? (
              <ul className="">
                {user ? (
                  <Link onClick={() => signout()}>
                    <button
                      className={`ml10 ${styles.myButton}`}
                    >
                      <img
                        src={userPictureUrl}
                        alt="avatar utilisateur"
                        className="avatar"
                      ></img>
                      <span>Deconnexion</span>
                    </button>
                  </Link>
                ) : (
                  ""
                )}
                {userGoogle && Object.keys(userGoogle).length !== 0 ? (
                  <Link onClick={() => signoutGoogle()}>
                    <button
                      className={`ml10 ${styles.myButton}`}
                    >
                      <img
                        src={userPictureUrl}
                        alt="avatar utilisateur google"
                        className="avatar"
                      ></img>
                      <span>Deconnexion</span>
                    </button>
                  </Link>
                ) : (
                  ""
                )}
                {userFacebook && Object.keys(userFacebook).length !== 0 ? (
                  <Link onClick={() => signoutFacebook()}>
                    <button
                      className={`${styles.myButton}`}
                    >
                      {" "}
                      <img
                        src={userPictureUrl}
                        alt="avatar utilisateur facebook"
                        className="avatar"
                      ></img>
                      <span>Deconnexion</span>
                    </button>
                  </Link>
                ) : (
                  ""
                )}
              </ul>
            ) : (
              <ul>
                <Link to="/signin">
                  <button type="button" className={`mr10 btn btn-primary`}>
                    <i className="fas fa-right-to-bracket m5 m5"></i>
                    <span>Connexion</span>
                  </button>
                </Link>
              </ul>
            )}
          </ul>
        </div>
      </div>
    </header>
  );
}
