import { useContext } from "react";
import { AuthContext } from "../../../context";
import { Navigate } from "react-router-dom";

export default function InfoUser() {
  const { user, userFacebook, userGoogle } = useContext(AuthContext);

  let userPictureUrl = "";

  if (Object.keys(userGoogle).length !== 0) {
    // Utilisateur connecté avec Google
    userPictureUrl = userGoogle.picture;
  } else if (Object.keys(userFacebook).length !== 0) {
    // Utilisateur connecté avec Facebook
    userPictureUrl = userFacebook.picture.data.url;
  }

  return (
    <>
      {user ||
      (userGoogle && Object.keys(userGoogle).length !== 0) ||
      (userFacebook && Object.keys(userFacebook).length !== 0) ? (
        <div className="backG container animate__animated animate__bounce">
          <div className="row">
            <div className="col-2">
            <img src={userPictureUrl} alt="avatar utilisateur" className="avatar"></img>
            </div>
            <div className="col-10 text-center">
              <h3>
                Bienvenue{" "}
                {userGoogle.name || userFacebook.name || user[0].pseudo}
              </h3>
            </div>
          </div>
          <div className="row">
            <p className="col-6">
    
              {user
                ? "Compte personnel"
                : Object.keys(userGoogle).length !== 0
                ? "Compte Google"
                : "votre Compte Facebook"}
            </p>
            <p className="col-6">{userFacebook.email || userGoogle.email}</p>
          </div>
        </div>
      ) : (
        <Navigate to="/signin" />
      )}
    </>
  );
}
