import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../../context";
import { Navigate } from "react-router-dom";
import MapOfGoogle from "./componants/MapOfGoogle";
import 'animate.css';
import AlertSweet from "../../components/alert/AlertSweet";


export default function Homepage() {
  const { user, userGoogle, userFacebook } = useContext(AuthContext);
  const [showAlert, setShowAlert] = useState(false);

  useEffect(() => {
    if (user || userFacebook || userGoogle) {
      setShowAlert(true);
    }
  }, [user, userGoogle, userFacebook]);

  const handleAlertClose = () => {
    setShowAlert(false);
  };

  return (
    <>
      {user ||
      (userGoogle && Object.keys(userGoogle).length !== 0) ||
      (userFacebook && Object.keys(userFacebook).length !== 0) ? (
        <div className="container">
            <h1 className="text-center mt-3 mb-2">Les sorties à faire autour de vous !</h1>
            <MapOfGoogle className='animate__animated animate__bounce'/>
        </div>
      ) : (
        <Navigate to="/signin" />
      )}
      {showAlert && (
        <AlertSweet
          title={`Bravo ${userGoogle.name}`}
          message={`Vous êtes connecté avec votre compte ${userGoogle.email}`}
          onClose={handleAlertClose}
        />
      )}
    </>
  );
}
