import React, { useContext, useState } from "react";
import { useForm } from "react-hook-form";
import * as Yup from "yup";
import { AuthContext } from "../../context/AuthContext";
import { yupResolver } from "@hookform/resolvers/yup";
import { Link, Navigate, useNavigate } from "react-router-dom";
import { GoogleLogin } from "@react-oauth/google";
import jwt_decode from "jwt-decode";
import FacebookLogin from "react-facebook-login";
//import { createUserGoogle } from "../../apis/users";

export default function SignIn() {
  const { signin, user, userFacebook, userGoogle } = useContext(AuthContext);
  console.log(userFacebook);

  const navigate = useNavigate();

  console.log(userGoogle);

  const validationSchema = Yup.object().shape({
    email: Yup.string()
      .email("Email non valide")
      .required("Ce champ doit être saisi"),
    password: Yup.string()
      .required("Required")
      .min(6, "Le mot de passe doit contenir 6 caractères min."),
  });

  const initialValues = {
    email: "",
    password: "",
  };

  const {
    handleSubmit,
    register,
    formState: { errors, isSubmitting },
    setError,
    clearErrors,
  } = useForm({
    initialValues,
    resolver: yupResolver(validationSchema),
  });

  const submit = handleSubmit(async (values) => {
    try {
      clearErrors();
      console.log(values);
      await signin(values);
      navigate("/");
    } catch (message) {
      setError("generic", { type: "generic", message });
    }
  });

  const handleGoogleLoginSuccess = (credentialResponse) => {
    console.log(credentialResponse.credential);
    let googleToken = credentialResponse.credential;
    let decoded = jwt_decode(googleToken);
    document.getElementById("signInGoogle").hidden = true;
    localStorage.setItem("userGoogle", JSON.stringify(decoded));
    window.location.reload();
  };

  const responseFacebook = (response) => {
    console.log(response);
    window.location.reload();

  };

  function handleSignOut(event) {
    document.getElementById("signInGoogle").hidden = false;
    localStorage.removeItem("userGoogle");
  }

  const handleGoogleLoginError = () => {
    console.log("Login Failed");
  };

  return (
    <>
      {user || (userGoogle && Object.keys(userGoogle).length !== 0) || (userFacebook && Object.keys(userFacebook).length !== 0) ? (
        <Navigate to="/" />
      ) : (
        <div className="d-flex justify-content-center">
          <div className={` m30`}>
            <h1 className="text-center">Connexion</h1>
            <form
              onSubmit={submit}
              className="d-flex flex-column justify-content-center align-items-center p20"
            >
              <div className="d-flex flex-column">
                <label htmlFor="email" className="mb10 pl20">
                  Email:
                </label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  {...register("email")}
                />
                {errors?.email && (
                  <p className="form-error">{errors.email.message}</p>
                )}
              </div>

              <div className="d-flex flex-column mt10 mb20">
                <label htmlFor="password" className="m-2">
                  Mot de passe :
                </label>
                <input
                  type="password"
                  className="form-control"
                  name="password"
                  {...register("password")}
                />
                {errors.password && (
                  <p className="form-error">{errors.password.message}</p>
                )}
              </div>
              {errors.generic && (
                <p className="form-error">{errors.generic.message}</p>
              )}
              <div className="mt-2">
                <button disabled={isSubmitting} className="btn btn-primary">
                  Se connecter
                </button>
              </div>
            </form>

            <div className="d-flex flex-column">
              {userGoogle && (
                <div>
                  <img src={userGoogle.picture}></img>
                  <h3>{userGoogle.name}</h3>
                  {Object.keys(userGoogle).length !== 0 && (
                    <button onClick={(e) => handleSignOut(e)}>
                      Se déconnecter
                    </button>
                  )}
                </div>
              )}
              <div id="signInGoogle">
                <GoogleLogin
                  buttonText="Connexion avec Google"
                  onSuccess={handleGoogleLoginSuccess}
                  onError={handleGoogleLoginError}
                  cookiePolicy={"single_host_origin"}
                />
              </div>

              <FacebookLogin
                appId="6447709785249838"
                
                fields="name,email,picture"
                callback={responseFacebook}
                //cssClass="my-facebook-button-class"
                icon="fa-facebook"
              />

              <Link to="/SignUp">
                <p>
                  Si vous voulez créer un compte sur notre site inscrivez-vous
                  ici
                </p>
              </Link>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
