import React, { useCallback, useContext, useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import * as Yup from "yup";
import { AuthContext } from "../../context/AuthContext";
import { yupResolver } from "@hookform/resolvers/yup";
import { Navigate, useNavigate } from "react-router-dom";
import { GoogleLogin } from "@react-oauth/google";
import jwt_decode from "jwt-decode";
//import { createUserGoogle } from "../../apis/users";
import { LoginSocialFacebook } from "reactjs-social-login";
import { FacebookLoginButton } from "react-social-login-buttons";
import { FacebookProvider } from "react-facebook";
import FacebookLogin from "react-facebook-login";



export default function SignIn() {

  const REDIRECT_URI ='https://localhost:3000/signin';
  const [provider, setProvider] = useState('');
  const [profile, setProfile] = useState();

  const onLoginStart = useCallback(() => {
    alert('login start');
  }, []);

  const onLogoutSuccess = useCallback(() => {
    setProfile(null);
    setProvider('');
    alert('logout success');
  }, []);

  const onLogout = useCallback(() => {}, []);


  const responseFacebook = (response) => {
    console.log(response);
  };

  const responseGoogle = (response) => {
    console.log(response);
  };

  const { signin, user } = useContext(AuthContext);

  const navigate = useNavigate();

  const [userGoogle, setUserGoogle] = useState({});
  console.log(userGoogle);

  const validationSchema = Yup.object().shape({
    email: Yup.string()
      .email("Email non valide")
      .required("Ce champ doit être saisi"),
    password: Yup.string()
      .required("Required")
      .min(6, "Le mot de passe doit contenir 6 caractères min."),
  });

  const initialValues = {
    email: "",
    password: "",
  };

  const {
    handleSubmit,
    register,
    formState: { errors, isSubmitting },
    setError,
    clearErrors,
  } = useForm({
    initialValues,
    resolver: yupResolver(validationSchema),
  });

  const submit = handleSubmit(async (values) => {
    try {
      clearErrors();
      console.log(values);
      await signin(values);
      navigate("/");
    } catch (message) {
      setError("generic", { type: "generic", message });
    }
  });

  const handleGoogleLoginSuccess = (credentialResponse) => {
    console.log(credentialResponse.credential);
    let googleToken = credentialResponse.credential;
    let decoded = jwt_decode(googleToken);
    setUserGoogle(decoded);
    document.getElementById("signInGoogle").hidden = true;
    localStorage.setItem("userGoogle", JSON.stringify(decoded));
    try {
      clearErrors();
      //console.log(decoded);
      // createUserGoogle(decoded);
      // navigate("/");
    } catch (message) {
      setError("generic", { type: "generic", message });
    }
  };

  function handleSignOut(event) {
    setUserGoogle({});
    document.getElementById("signInGoogle").hidden = false;
    localStorage.removeItem("userGoogle");
  }

  const handleGoogleLoginError = () => {
    console.log("Login Failed");
  };

  return (




    
    <>


<>

      <div className={`App ${provider && profile ? 'hide' : ''}`}>
        <h1 className="title">ReactJS Social Login</h1>
        <LoginSocialFacebook
          appId={process.env.REACT_APP_FB_APP_ID || ''}
          fieldsProfile={
            'id,first_name,last_name,middle_name,name,name_format,picture,short_name,email,gender'
          }
          onLoginStart={onLoginStart}
          onLogoutSuccess={onLogoutSuccess}
          redirect_uri={REDIRECT_URI}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={err => {
            console.log(err);
          }}
        >
          <FacebookLoginButton />
        </LoginSocialFacebook>

        <LoginSocialGoogle
          client_id={process.env.REACT_APP_GG_APP_ID || ''}
          onLoginStart={onLoginStart}
          redirect_uri={REDIRECT_URI}
          scope="openid profile email"
          discoveryDocs="claims_supported"
          access_type="offline"
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={err => {
            console.log(err);
          }}
        >
          <GoogleLoginButton />
        </LoginSocialGoogle>

        <LoginSocialApple
          client_id={process.env.REACT_APP_APPLE_ID || ''}
          scope={'name email'}
          redirect_uri={REDIRECT_URI}
          onLoginStart={onLoginStart}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={err => {
            console.log(err);
          }}
        >
          <AppleLoginButton />
        </LoginSocialApple>

        <LoginSocialAmazon
          client_id={process.env.REACT_APP_AMAZON_APP_ID || ''}
          redirect_uri={REDIRECT_URI}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={(err: any) => {
            console.log(err);
          }}
          onLoginStart={onLoginStart}
        >
          <AmazonLoginButton />
        </LoginSocialAmazon>

        <LoginSocialInstagram
          client_id={process.env.REACT_APP_INSTAGRAM_APP_ID || ''}
          client_secret={process.env.REACT_APP_INSTAGRAM_APP_SECRET || ''}
          redirect_uri={REDIRECT_URI}
          onLoginStart={onLoginStart}
          onLogoutSuccess={onLogoutSuccess}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={(err: any) => {
            console.log(err);
          }}
        >
          <InstagramLoginButton />
        </LoginSocialInstagram>

        <LoginSocialMicrosoft
          client_id={process.env.REACT_APP_MICROSOFT_APP_ID || ''}
          redirect_uri={REDIRECT_URI}
          onLoginStart={onLoginStart}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={(err: any) => {
            console.log(err);
          }}
        >
          <MicrosoftLoginButton />
        </LoginSocialMicrosoft>

        <LoginSocialLinkedin
          client_id={process.env.REACT_APP_LINKEDIN_APP_ID || ''}
          client_secret={process.env.REACT_APP_LINKEDIN_APP_SECRET || ''}
          redirect_uri={REDIRECT_URI}
          onLoginStart={onLoginStart}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={(err: any) => {
            console.log(err);
          }}
        >
          <LinkedInLoginButton />
        </LoginSocialLinkedin>

        <LoginSocialGithub
          client_id={process.env.REACT_APP_GITHUB_APP_ID || ''}
          client_secret={process.env.REACT_APP_GITHUB_APP_SECRET || ''}
          redirect_uri={REDIRECT_URI}
          onLoginStart={onLoginStart}
          onLogoutSuccess={onLogoutSuccess}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={(err: any) => {
            console.log(err);
          }}
        >
          <GithubLoginButton />
        </LoginSocialGithub>
        <LoginSocialPinterest
          client_id={process.env.REACT_APP_PINTEREST_APP_ID || ''}
          client_secret={process.env.REACT_APP_PINTEREST_APP_SECRET || ''}
          redirect_uri={REDIRECT_URI}
          onLoginStart={onLoginStart}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={(err: any) => {
            console.log(err);
          }}
          className="pinterest-btn"
        >
          <div className="content">
            <div className="icon">
              <PinterestLogo />
            </div>
            <span className="txt">Login With Pinterest</span>
          </div>
        </LoginSocialPinterest>

        <LoginSocialTwitter
          client_id={process.env.REACT_APP_TWITTER_V2_APP_KEY || ''}
          // client_secret={process.env.REACT_APP_TWITTER_V2_APP_SECRET || ''}
          redirect_uri={REDIRECT_URI}
          onLoginStart={onLoginStart}
          onLogoutSuccess={onLogoutSuccess}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={(err: any) => {
            console.log(err);
          }}
        >
          <TwitterLoginButton />
        </LoginSocialTwitter>
      </div>
    </>



      {user || Object.keys(userGoogle).length !== 0 ? (
        <Navigate to="/" />
      ) : (
        <div className="d-flex justify-content-center">
          <div className={` m30`}>
            <h1 className="text-center">Connexion</h1>
            <form
              onSubmit={submit}
              className="d-flex flex-column justify-content-center align-items-center p20"
            >
              <div className="d-flex flex-column">
                <label htmlFor="email" className="mb10 pl20">
                  Email:
                </label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  {...register("email")}
                />
                {errors?.email && (
                  <p className="form-error">{errors.email.message}</p>
                )}
              </div>

              <div className="d-flex flex-column mt10 mb20">
                <label htmlFor="password" className="m-2">
                  Mot de passe :
                </label>
                <input
                  type="password"
                  className="form-control"
                  name="password"
                  {...register("password")}
                />
                {errors.password && (
                  <p className="form-error">{errors.password.message}</p>
                )}
              </div>
              {errors.generic && (
                <p className="form-error">{errors.generic.message}</p>
              )}
              <div className="mt-2">
                <button disabled={isSubmitting} className="btn btn-primary">
                  Se connecter
                </button>
              </div>
            </form>

            <div className="d-flex flex-column">
              {userGoogle && (
                <div>
                  <img src={userGoogle.picture}></img>
                  <h3>{userGoogle.name}</h3>
                  {Object.keys(userGoogle).length != 0 && (
                    <button onClick={(e) => handleSignOut(e)}>
                      Se déconnecter
                    </button>
                  )}
                </div>
              )}
              <div id="signInGoogle">
                <GoogleLogin
                  buttonText="Connexion avec Google"
                  onSuccess={handleGoogleLoginSuccess}
                  onError={handleGoogleLoginError}
                  cookiePolicy={"single_host_origin"}
                />
              </div>
              <div>
                <FacebookProvider appId="975586743885156">
                  <LoginSocialFacebook
                    onResolve={(response) => {
                      console.log(response);
                    }}
                    onReject={(error) => {
                      console.log(error);
                    }}
                  >
                    <FacebookLoginButton />
                  </LoginSocialFacebook>
                </FacebookProvider>
              </div>

              <LoginSocialFacebook
          appId="975586743885156"
          fieldsProfile={
            'id,first_name,last_name,middle_name,name,name_format,picture,short_name,email,gender'
          }
          onLoginStart={onLoginStart}
          onLogoutSuccess={onLogoutSuccess}
          redirect_uri={REDIRECT_URI}
          onResolve={({ provider, data }: IResolveParams) => {
            setProvider(provider);
            setProfile(data);
          }}
          onReject={err => {
            console.log(err);
          }}
        >
          <FacebookLoginButton />
        </LoginSocialFacebook>


              <div
                className="fb-like"
                data-share="true"
                data-width="450"
                data-show-faces="true"
              ></div>

            </div>
          </div>
        </div>
      )}
    </>
  );
}

