const API_GOOGLE = "/api/googleMap";

export async function touristPlace(location) {
    try {
        const response = await fetch (`${API_GOOGLE}/searchNearbyPlaces?lat=${location.lat}&lng=${location.lng}`);
        return response.json();
    } catch (error) {
        console.error('Erreur de recherche de lieux touristiques', error);
    }
}

export async function detailsPlace(location) {
    try {
        const response = await fetch (`${API_GOOGLE}/searchDetail?lat=${location.lat}&lng=${location.lng}`)
        return response.json();
        log
    } catch (error) {
        console.error('Erreur recherche de détails sur un lieu');
    }
}
