import React, { useContext } from "react";
import styles from "./Header.module.scss";
import { useState } from "react";
import MobileMenu from "./components/MobileMenu";
import { Link } from "react-router-dom";
import { AuthContext } from "../../context";
import logo from "../../assets/png/logo.png";

export default function Header() {
  const {
    userGoogle,
    user,
    signout,
    signoutGoogle,
    userFacebook,
    signoutFacebook,
  } = useContext(AuthContext);
  const [showMenu, setShowMenu] = useState(false);
  const [count, setCount] = useState(0);

  let userPictureUrl = "";

  if (Object.keys(userGoogle).length !== 0) {
    // Utilisateur connecté avec Google
    userPictureUrl = userGoogle.picture;
  } else if (Object.keys(userFacebook).length !== 0) {
    // Utilisateur connecté avec Facebook
    userPictureUrl = userFacebook.picture.data.url;
  }

  function addCount() {
    setCount(count + 1);
  }
  function handleClick() {
    if (count === 0) {
      setShowMenu(true);
      addCount();
    } else {
      setShowMenu(false);
      setCount(0);
    }
  }

  return (
    <header className={`${styles.header}`}>
      <div className="d-flex justify-content-center">
        <ul className={`${styles.desktopHeader}`}>
          {user ||
          (userGoogle && Object.keys(userGoogle).length !== 0) ||
          (userFacebook && Object.keys(userFacebook).length !== 0) ? (
            <ul className="d-flex justify-content-round">
              <Link to="/">
                <button className="btn-primary las la-home">
                  <span>Accueil</span>
                </button>
              </Link>
              <div className="d-flex justify-content-center">
                <img src={logo} alt="logo 20 minutes" className="m-3 p-3"></img>
              </div>
              {user ? (
                <Link onClick={() => signout()}>
                  <button className="btn-primary-reverse ml10 las la-sign-out-alt">
                    <span className={`${styles.te}`}>Deconnexion</span>
                  </button>
                </Link>
              ) : (
                ""
              )}
              {userGoogle && Object.keys(userGoogle).length !== 0 ? (
                <Link onClick={() => signoutGoogle()}>
                  <button className="btn-primary-reverse ml10 las la-sign-out-alt">
                    <span className={`${styles.te}`}>Deconnexion</span>
                  </button>
                </Link>
              ) : (
                ""
              )}
              {userFacebook && Object.keys(userFacebook).length !== 0 ? (
                <Link onClick={() => signoutFacebook()}>
                  <button className="btn-primary-reverse ml10 las la-sign-out-alt">
                    <span className={`${styles.te}`}>Deconnexion</span>
                  </button>
                </Link>
              ) : (
                ""
              )}
            </ul>
          ) : (
            <ul>
              <Link to="/signin">
                <button type="button" className={`mr10 btn btn-primary`}>
                  <i className="fas fa-right-to-bracket m5 m5"></i>
                  <span>Connexion</span>
                </button>
              </Link>
            </ul>
          )}
        </ul>
        <i
          onClick={handleClick}
          className={`las la-bars mr10 positionFixed`}
        ></i>
        {showMenu && (
          <>
            <MobileMenu />
          </>
        )}
      </div>
      {user ||
      (userGoogle && Object.keys(userGoogle).length !== 0) ||
      (userFacebook && Object.keys(userFacebook).length !== 0) ? (
        <div className="container">
          <div className="row">
            <div className="col-8">
              <h2>
                Bienvenue{" "}
                {userGoogle.name || userFacebook.name || user[0].pseudo}
              </h2>
            </div>
            <div className="col-4">
              <img src={userPictureUrl} alt="avatar utilisateur"></img>
            </div>
          </div>
          <div className="row">
            <p className="col-6">
              Vous êtes connecté avec{" "}
              {user
                ? "votre Compte personnel"
                : Object.keys(userGoogle).length !== 0
                ? "votre Compte Google"
                : "votre Compte Facebook"}
            </p>
            <p className="col-6">{userFacebook.email || userGoogle.email}</p>
          </div>
          <div>
            <h1>20 sorties à faire autour de vous !</h1>
            <MapOfGoogle />
          </div>
        </div>
      ) : (
        <Navigate to="/signin" />
      )}
    </header>
  );
}
