import { useContext } from "react";
import { AuthContext } from "../../context";
import { Navigate } from "react-router-dom";
import GoogleMap from "./componants/GoogleMap";

export default function Homepage() {
  const { user, userGoogle, userFacebook } = useContext(AuthContext);
  console.log(userGoogle);
  console.log(userFacebook);
  console.log(user);

  const apiKey = "AIzaSyAVgXsiMBgIY7aqZBovO2wY2gD5CDQT7gQ";
  const location = { lat: 50.7521, lng: 2.7001 }; 

  let userPictureUrl = "";

  if (Object.keys(userGoogle).length !== 0) {
    // Utilisateur connecté avec Google
    userPictureUrl = userGoogle.picture;
  } else if (Object.keys(userFacebook).length !== 0) {
    // Utilisateur connecté avec Facebook
    userPictureUrl = userFacebook.picture.data.url;
  }

  return (
    <>
      {user ||
      (userGoogle && Object.keys(userGoogle).length !== 0) ||
      (userFacebook && Object.keys(userFacebook).length !== 0) ? (
        <div className="container">
            <div className="row">
              <div className="col-6 text-end">
                <h2>
                  Bienvenue{" "}
                  {userGoogle.name || userFacebook.name || user[0].pseudo}
                </h2>
                <p>
                  Votre adresse mail : {userFacebook.email || userGoogle.email}
                </p>
              </div>
              <div className="col-6 text-center">
                <img src={userPictureUrl} alt="avatar utilisateur"></img>
              </div>
            </div>
            <div className="row">
              <p>
                Vous êtes connecté avec{" "}
                {user
                  ? "votre Compte personnel"
                  : Object.keys(userGoogle).length !== 0
                  ? "votre Compte Google"
                  : "votre Compte Facebook"}
              </p>
            </div>


          <div>
            <h1>Lieu touristique</h1>
            <GoogleMap apiKey={apiKey} location={location} />
          </div>
        </div>
      ) : (
        <Navigate to="/signin" />
      )}
    </>
  );
}
