import React, { useContext } from "react";
import styles from "./Header.module.scss";
import { useState } from "react";
import MobileMenu from "./components/MobileMenu";
import { Link } from "react-router-dom";
import { AuthContext } from "../../context";
import logo from "../../assets/png/logo.png";

export default function Header() {
  const {
    userGoogle,
    user,
    signout,
    signoutGoogle,
    userFacebook,
    signoutFacebook,
  } = useContext(AuthContext);
  const [showMenu, setShowMenu] = useState(false);
  const [count, setCount] = useState(0);

  function addCount() {
    setCount(count + 1);
  }
  function handleClick() {
    if (count === 0) {
      setShowMenu(true);
      addCount();
    } else {
      setShowMenu(false);
      setCount(0);
    }
  }

  return (
    <header className={`${styles.header}`}>
      <div className="d-flex justify-content-center">
        <ul className={`${styles.desktopHeader}`}>
          {user ||
          (userGoogle && Object.keys(userGoogle).length !== 0) ||
          (userFacebook && Object.keys(userFacebook).length !== 0) ? (
            <ul className="d-flex justify-content-round">
              <div className="d-flex justify-content-center">
                <img src={logo} alt="logo 20 sorties" className="m-3 p-3"></img>
              </div>
              {user ? (
                <Link onClick={() => signout()}>
                  <button className="ml10 las la-sign-out-alt">
                    <span className={`${styles.myButton}`}>Deconnexion</span>
                  </button>
                </Link>
              ) : (
                ""
              )}
              {userGoogle && Object.keys(userGoogle).length !== 0 ? (
                <Link onClick={() => signoutGoogle()}>
                  <button className="ml10 las la-sign-out-alt">
                    <span className={`${styles.myButton}`}>Deconnexion</span>
                  </button>
                </Link>
              ) : (
                ""
              )}
              {userFacebook && Object.keys(userFacebook).length !== 0 ? (
                <Link onClick={() => signoutFacebook()}>
                  <button className="ml10 las la-sign-out-alt">
                    <span className={`${styles.te}`}>Deconnexion</span>
                  </button>
                </Link>
              ) : (
                ""
              )}
            </ul>
          ) : (
            <ul>
              <Link to="/signin">
                <button type="button" className={`mr10 btn btn-primary`}>
                  <i className="fas fa-right-to-bracket m5 m5"></i>
                  <span>Connexion</span>
                </button>
              </Link>
            </ul>
          )}
        </ul>
      </div>
    </header>
  );
}
