import React, { useContext } from "react";
import styles from "./Header.module.scss";
import { useState } from "react";
import MobileMenu from "./components/MobileMenu";
import { Link } from "react-router-dom";
import { AuthContext } from "../../context";

export default function Header() {
  const { userGoogle, user, signout, signoutGoogle, userFacebook, signoutFacebook} = useContext(AuthContext);
  const [showMenu, setShowMenu] = useState(false);
  const [count, setCount] = useState(0);

  function addCount() {
    setCount(count + 1);
  }
  function handleClick() {
    if (count === 0) {
      setShowMenu(true);
      addCount();
    } else {
      setShowMenu(false);
      setCount(0);
    }
  }

  if (Object.keys(userGoogle).length !== 0) {
    // Utilisateur connecté avec Google
    userPictureUrl = userGoogle.picture;
  } else if (Object.keys(userFacebook).length !== 0) {
    // Utilisateur connecté avec Facebook
    userPictureUrl = userFacebook.picture.data.url;
  }


  return (
    <header className={`${styles.header}`}>
        <div className="row">
              <div className="col-8 text-center">
                <h2>
                  Bienvenue {userGoogle.name || userFacebook.name || user[0].pseudo}
                </h2>
                <p>
                  {userFacebook.email || userGoogle.email}
                </p>
              </div>
            </div>
            <div className="row">
              <p>
                Vous êtes connecté avec
                {user
                  ? "votre Compte personnel"
                  : Object.keys(userGoogle).length !== 0
                  ? "votre Compte Google"
                  : "votre Compte Facebook"}
              </p>
            </div>
          <div></div>

      <div className="d-flex justify-content-center">
        


        <ul className={`${styles.desktopHeader}`}>
          {user || (userGoogle && Object.keys(userGoogle).length !== 0) || (userFacebook && Object.keys(userFacebook).length !== 0) ? (

            <ul className="d-flex justify-content-evenly">
              <Link to="/">
                <button className="btn-primary las la-home">
                  <span>Accueil</span>
                </button>
              </Link>
              {user ? (
                <Link onClick={() => signout()}>
                  <button className="btn-primary-reverse ml10 las la-sign-out-alt">
                    <span className={`${styles.te}`}>Deconnexion</span>
                  </button>
                </Link>
              ) : (
                ""
              )}
              {userGoogle && Object.keys(userGoogle).length !== 0 ? (
                <Link onClick={() => signoutGoogle()}>
                  <button className="btn-primary-reverse ml10 las la-sign-out-alt">
                    <span className={`${styles.te}`}>Deconnexion</span>
                  </button>
                </Link>
              ) : (
                ""
              )}
               {userFacebook && Object.keys(userFacebook).length !== 0 ? (
                <Link onClick={() => signoutFacebook()}>
                  <button className="btn-primary-reverse ml10 las la-sign-out-alt">
                    <span className={`${styles.te}`}>Deconnexion</span>
                  </button>
                </Link>
              ) : (
                ""
              )}
            </ul>
          ) : (
            <ul>
              <Link to="/signin">
                <button
                  type="button"
                  className={`mr10 btn btn-primary`}
                >
                  <i className="fas fa-right-to-bracket m5 m5"></i>
                  <span>Connexion</span>
                </button>
              </Link>
            </ul>
          )}
        </ul>
        <i
          onClick={handleClick}
          className={`las la-bars mr10 positionFixed`}
        ></i>
        {showMenu && (
          <>
            <MobileMenu />
          </>
        )}
      </div>

      <div className="d-flex justify-content-center mt-4">
        <Link to="/">
          <p>LOGO</p>
        </Link>
      </div>
    </header>
  );
}
