const API_GOOGLE = "/api/googleMap";

export async function touristPlace(location) {
    try {
        const response = await fetch (`${API_GOOGLE}/searchNearbyPlaces?lat=${location.lat}&lng=${location.lng}`);
        return response.json();
    } catch (error) {
        console.error('Erreur de recherche de lieux touristiques', error);
    }
}

export async function detailsPlace() {
    try {
        const response = await fetch (`https://maps.googleapis.com/maps/api/place/details/json?fields=name%2Crating%2Cformatted_phone_number&place_id=ChIJN1t_tDeuEmsRUsoyG83frY4&key=${process.env.REACT_APP_GOOGLE_API_KEY}`)
        return response.json();
    } catch (error) {
        console.error('Erreur recherche de détails sur ');
    }
}
