import {
  GoogleMap,
  InfoWindow,
  Marker,
  useLoadScript,
} from "@react-google-maps/api";
import { useEffect, useRef, useState } from "react";
import styles from "./MapOfGoogle.module.scss";
import { detailsPlace } from "../../../apis/mapOfGoogle";
import Loading from "../../../components/Loading/Loading";

const MapOfGoogle = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [places, setPlaces] = useState([]);
  console.log(places);
  const [selectedPlace, setSelectedPlace] = useState(null);
  console.log(selectedPlace);

  const placeRefs = useRef([]);
  console.log(placeRefs);

  const searchNearbyPlaces = async (location) => {
    try {
      const data = await detailsPlace(location);
      //console.log(data);
      setPlaces(data);
    } catch (error) {
      console.error("Error searching for places:", error);
    }
  };

  //fonction permettant de géolocalisé l'utilisateur grâce à son navigateur
  const getUserLocation = () => {
    return new Promise((resolve, reject) => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            resolve({
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            });
          },
          (error) => {
            reject(error);
          }
        );
      } else {
        reject(new Error("Geolocation is not supported by this browser."));
      }
    });
  };

  const { isLoaded } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_API_KEY,
  });

  useEffect(() => {
    if (isLoaded) {
      getUserLocation()
        .then((location) => {
          setUserLocation(location);
          searchNearbyPlaces(location);
        })
        .catch((error) => {
          console.error("Error getting user location:", error);
        });
    }
  }, [isLoaded]);

  /** @type {*image svg pointer} */
  const customMarker = (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 48 48" width="48px" height="48px">
      <path fill="#48b564" d="M35.76,26.36h0.01c0,0-3.77,5.53-6.94,9.64c-2.74,3.55-3.54,6.59-3.77,8.06	C24.97,44.6,24.53,45,24,45s-0.97-0.4-1.06-0.94c-0.23-1.47-1.03-4.51-3.77-8.06c-0.42-0.55-0.85-1.12-1.28-1.7L28.24,22l8.33-9.88	C37.49,14.05,38,16.21,38,18.5C38,21.4,37.17,24.09,35.76,26.36z" />
      <path fill="#fcc60e" d="M28.24,22L17.89,34.3c-2.82-3.78-5.66-7.94-5.66-7.94h0.01c-0.3-0.48-0.57-0.97-0.8-1.48L19.76,15	c-0.79,0.95-1.26,2.17-1.26,3.5c0,3.04,2.46,5.5,5.5,5.5C25.71,24,27.24,23.22,28.24,22z" />
      <path fill="#2c85eb" d="M28.4,4.74l-8.57,10.18L13.27,9.2C15.83,6.02,19.69,4,24,4C25.54,4,27.02,4.26,28.4,4.74z" />
      <path fill="#ed5748" d="M19.83,14.92L19.76,15l-8.32,9.88C10.52,22.95,10,20.79,10,18.5c0-3.54,1.23-6.79,3.27-9.3	L19.83,14.92z" />
      <path fill="#5695f6" d="M28.24,22c0.79-0.95,1.26-2.17,1.26-3.5c0-3.04-2.46-5.5-5.5-5.5c-1.71,0-3.24,0.78-4.24,2	L28.4,4.74	c3.59,1.22,6.53,3.91,8.17,7.38L28.24,22z" />
    </svg>
  );

  const handleMarkerMouseOver = (place) => {
    setSelectedPlace(place);
  };

  const handleMarkerClick = (place) => {
    setSelectedPlace(place);
    console.log(place);
    // Récupérer l'index du lieu cliqué
    const index = places.findIndex((p) => p.place_id === place.place_id);
    console.log(index);
    // Effectuer le défilement jusqu'à l'élément correspondant
    const element = placeRefs.current[index];
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <div>
      <div className={`${styles.app}`}>
        {!isLoaded ? (
          <Loading />
        ) : (
          <>
            <GoogleMap
              mapContainerClassName={`${styles.mapContainer}`}
              center={userLocation}
              zoom={12}
              style={{ width: "50%", height: "100vh" }}
            >
              {places.map((place) => (
                <div key={place.id}>
                  <Marker
                    position={{
                      lat: place.geometry.location.lat,
                      lng: place.geometry.location.lng,
                    }}
                    icon={customMarker}
                    onMouseOver={() => handleMarkerMouseOver(place)}
                    onClick={() => handleMarkerClick(place)}
                  />
                </div>
              ))}
              {selectedPlace && (
                <InfoWindow
                  position={{
                    lat: selectedPlace.geometry.location.lat,
                    lng: selectedPlace.geometry.location.lng,
                  }}
                  onCloseClick={() => setSelectedPlace(null)}
                >
                  <div className={`${styles.infoWindow2}`}>
                    <div>{selectedPlace.name}</div>
                    <div>{selectedPlace.vicinity}</div>
                  </div>
                </InfoWindow>
              )}
            </GoogleMap>

            {selectedPlace && (
              <div className={styles.infoWindow}>
                <h3>{selectedPlace.name}</h3>
                <p>{selectedPlace.address}</p>
              </div>
            )}
          </>
        )}
      </div>
      <div className={``}>
        {places.map((place, i) => (
          <div
            key={i}
            className={`${styles.cart} d-flex flex-column mt-5`}
            id={`place-${place.id}`}
          >
            <div className="row" ref={(ref) => (placeRefs.current[i] = ref)}>
              <div className="col-lg-5 col-md-5 col-sm-12">
                {place.photos && (
                  <>
                    <img
                      src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&maxheight=400&photo_reference=${place.photos[0].photo_reference}&key=${process.env.REACT_APP_GOOGLE_API_KEY}`}
                      alt={`${place.name}`}
                      className=" rounded mx-auto d-block "
                    />
                  </>
                )}
              </div>
              <div className={`${styles.carte} col-lg-7 col-md-7 col-sm-12`}>
                <div className="d-flex flex-column p-3 mr-2 pr-5">
                <a
                    className={`${styles.decoNone}`}
                    href={place.details.url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                  <h3 className=" text-center ">{place.name}</h3>

                    <p className="card-text">Adresse : {place.vicinity}</p>
                  </a>

                  <a
                    href={`tel:${place.details.formatted_phone_number}`}
                    className={`${styles.decoNone} text-center`}
                  >
                    {place.details.formatted_phone_number}
                  </a>

                  <div>
                    {place.rating && (
                      <p>
                        Note Google : {place.rating}/5 pour{" "}
                        {place.user_ratings_total} votes
                      </p>
                    )}
                  </div>

                  <div className="d-flex m-auto">
                    {place.details.website && (
                      <a
                        href={place.details.website}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="btn btn-primary decoNone"
                      >
                        Visiter le site
                      </a>
                    )}
                  </div>
                </div>
              </div>
            </div>

            <div className="row">
              <div className={`${styles.carte} col-lg-5 col-md-5 col-sm-12 `}>
                {place.details.opening_hours && (
                  <>
                    <h4 className="text-center mt-2"> Horaire d'ouverture </h4>
                    {place.details.opening_hours.weekday_text.map(
                      (index, i) => (
                        <div key={i}>{index}</div>
                      )
                    )}
                  </>
                )}
              </div>

              <div className={`col-lg-7 col-md-7 col-sm-12`}>
                {place.details.reviews && (
                  <div>
                    <h4 className="text-center mt-2">Avis</h4>
                    {place.details.reviews.map((review, i) => (
                      <div key={i} className={`${styles.carte} mt-2`}>
                        <div className="row">
                          <div className="col-6">{review.author_name} :</div>
                          <div className="col-6"> Note : {review.rating}/5</div>
                        </div>
                        <div>{review.text}</div>
                        <br></br>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MapOfGoogle;
