import styles from "./SignUp.module.scss";
import { useState } from "react";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
//import { useEffect } from "react";
//import { GoogleLogin, GoogleLogout } from "react-google-login";
//import { gapi } from "gapi-script";
import { createUser } from "../../apis/users";
import { useNavigate } from "react-router-dom";
import SweetAlert from "../../components/alert/AlertSweet";

export default function SignUp() {


<>

<div className={`App ${provider && profile ? 'hide' : ''}`}>
  <h1 className="title">ReactJS Social Login</h1>
  <LoginSocialFacebook
    appId="975586743885156"
    fieldsProfile={
      'id,first_name,last_name,middle_name,name,name_format,picture,short_name,email,gender'
    }
    onLoginStart={onLoginStart}
    onLogoutSuccess={onLogoutSuccess}
    redirect_uri={REDIRECT_URI}
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={err => {
      console.log(err);
    }}
  >
    <FacebookLoginButton />
  </LoginSocialFacebook>

  <LoginSocialGoogle
    client_id={process.env.REACT_APP_GG_APP_ID || ''}
    onLoginStart={onLoginStart}
    redirect_uri={REDIRECT_URI}
    scope="openid profile email"
    discoveryDocs="claims_supported"
    access_type="offline"
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={err => {
      console.log(err);
    }}
  >
    <GoogleLoginButton />
  </LoginSocialGoogle>

  <LoginSocialApple
    client_id={process.env.REACT_APP_APPLE_ID || ''}
    scope={'name email'}
    redirect_uri={REDIRECT_URI}
    onLoginStart={onLoginStart}
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={err => {
      console.log(err);
    }}
  >
    <AppleLoginButton />
  </LoginSocialApple>

  <LoginSocialAmazon
    client_id={process.env.REACT_APP_AMAZON_APP_ID || ''}
    redirect_uri={REDIRECT_URI}
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={(err: any) => {
      console.log(err);
    }}
    onLoginStart={onLoginStart}
  >
    <AmazonLoginButton />
  </LoginSocialAmazon>

  <LoginSocialInstagram
    client_id={process.env.REACT_APP_INSTAGRAM_APP_ID || ''}
    client_secret={process.env.REACT_APP_INSTAGRAM_APP_SECRET || ''}
    redirect_uri={REDIRECT_URI}
    onLoginStart={onLoginStart}
    onLogoutSuccess={onLogoutSuccess}
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={(err: any) => {
      console.log(err);
    }}
  >
    <InstagramLoginButton />
  </LoginSocialInstagram>

  <LoginSocialMicrosoft
    client_id={process.env.REACT_APP_MICROSOFT_APP_ID || ''}
    redirect_uri={REDIRECT_URI}
    onLoginStart={onLoginStart}
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={(err: any) => {
      console.log(err);
    }}
  >
    <MicrosoftLoginButton />
  </LoginSocialMicrosoft>

  <LoginSocialLinkedin
    client_id={process.env.REACT_APP_LINKEDIN_APP_ID || ''}
    client_secret={process.env.REACT_APP_LINKEDIN_APP_SECRET || ''}
    redirect_uri={REDIRECT_URI}
    onLoginStart={onLoginStart}
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={(err: any) => {
      console.log(err);
    }}
  >
    <LinkedInLoginButton />
  </LoginSocialLinkedin>

  <LoginSocialGithub
    client_id={process.env.REACT_APP_GITHUB_APP_ID || ''}
    client_secret={process.env.REACT_APP_GITHUB_APP_SECRET || ''}
    redirect_uri={REDIRECT_URI}
    onLoginStart={onLoginStart}
    onLogoutSuccess={onLogoutSuccess}
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={(err: any) => {
      console.log(err);
    }}
  >
    <GithubLoginButton />
  </LoginSocialGithub>
  <LoginSocialPinterest
    client_id={process.env.REACT_APP_PINTEREST_APP_ID || ''}
    client_secret={process.env.REACT_APP_PINTEREST_APP_SECRET || ''}
    redirect_uri={REDIRECT_URI}
    onLoginStart={onLoginStart}
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={(err: any) => {
      console.log(err);
    }}
    className="pinterest-btn"
  >
    <div className="content">
      <span className="txt">Login With Pinterest</span>
    </div>
  </LoginSocialPinterest>

  <LoginSocialTwitter
    client_id={process.env.REACT_APP_TWITTER_V2_APP_KEY || ''}
    // client_secret={process.env.REACT_APP_TWITTER_V2_APP_SECRET || ''}
    redirect_uri={REDIRECT_URI}
    onLoginStart={onLoginStart}
    onLogoutSuccess={onLogoutSuccess}
    onResolve={({ provider, data }: IResolveParams) => {
      setProvider(provider);
      setProfile(data);
    }}
    onReject={(err: any) => {
      console.log(err);
    }}
  >
    <TwitterLoginButton />
  </LoginSocialTwitter>
</div>
</>


  const navigate = useNavigate();


  function generateRandomUsername() {
    const letters = "abcdefghijklmnopqrstuvwxyz";
    let username = "";

    // génère les 5 premières lettres aléatoires
    for (let i = 0; i < 5; i++) {
      username += letters.charAt(Math.floor(Math.random() * letters.length));
    }

    // génère les 10 chiffres aléatoires
    for (let i = 0; i < 3; i++) {
      username += Math.floor(Math.random() * 3);
    }
    return username;
  }

  const [count, setCount] = useState(0);

  const yupSchema = yup.object({
    pseudo: yup
      .string()
      .required("Vous pouvez renseigner ce champs avec un pseudo"),
    email: yup
      .string()
      .email("Vous devez saisir un email valide")
      .required("Ce champ doit être renseigné"),
    password: yup
      .string()
      .required()
      .min(5, "Le mot de passe doit contenir au moins 5 caractères")
      .max(50, "Le mot de passe doit contenir au plus 50 caractères"),
    confirm_password: yup
      .string()
      .required("Le mot de passe doit être confirmé")
      .oneOf(
        [yup.ref("password"), ""],
        "Les mots de passe ne correspondent pas"
    ),
  });

  const defaultValues = {
    pseudo: generateRandomUsername(),
    firstname: "",
    email: "",
    password: "",
    name: "",
    places: [],
    condition: "",
  };

  function addCount() {
    setCount(count + 1);
    if (count > 10) {
      alert("trop d'essais");
    }
  }

  const {
    register,
    handleSubmit,
    setError,
    clearErrors,
    formState: { errors, isSubmitting },
  } = useForm({
    defaultValues,
    resolver: yupResolver(yupSchema),
  });

  const submit = handleSubmit(async (values) => {
    try {
      await createUser(values);
      SweetAlert("Bravo", "Vous êtes inscrit");
      navigate("/signin");
    } catch (message) {
      setError("generic", { type: "generic", message });
    }
  });

  return (
    <div className=" d-flex justify-content-center">
      <div className={`${styles.rectangle} m30`}>
        <h1 className="text-align-center">Inscription</h1>
        <form
          onSubmit={submit}
          className="d-flex flex-column justify-content-center align-items-center p20"
        >
          <div className="d-flex flex-column">
            <label className="mb10 pl20" htmlFor="pseudo">
              Pseudo :
            </label>
            <input type="text" id="pseudo" {...register("pseudo")} />
            {errors?.pseudo && <p>{errors.pseudo.message}</p>}
          </div>
          <div className="d-flex flex-column mt10">
            <label className="mb10 pl20" htmlFor="email">
              Adresse e-mail :
            </label>
            <input type="email" id="email" {...register("email")} />
            {errors?.email && <p>{errors.email.message}</p>}
          </div>
          <div className="d-flex flex-column mt10">
            <label className="mb10 pl20">Mot de passe :</label>
            <input type="password" id="password" {...register("password")} />
            {errors?.password && <p>{errors.password.message}</p>}
          </div>

          <div className="d-flex flex-column mt10 mb20">
            <label className="mb10 pl20" htmlFor="confirm_password">
              Confirmation du mot de passe :
            </label>
            <input
              type="password"
              id="confirm_password"
              {...register("confirm_password")}
            />
            {errors?.confirm_password && (
              <p>{errors.confirm_password.message}</p>
            )}
          </div>

          <button
            onClick={addCount}
            disabled={isSubmitting}
            className="btn btn-primary"
          >
            Inscription
          </button>

        </form>
      </div>
    </div>
  );
}
