import { useEffect, useState } from "react";
import { useLoaderData } from "react-router-dom";
import { signin as login } from "../../apis/auth";
import { signout as logout } from "../../apis/auth";
import { AuthContext } from "../../context";

export default function AuthProvider({ children }) {
  const initialUser = useLoaderData();
  const [user, setUser] = useState(initialUser);

  const [userGoogle, setUserGoogle] = useState({});
  const [userFacebook, setUserFacebook] = useState({});
  // console.log(userFacebook);
  // console.log(userGoogle);
  // console.log(user);



  useEffect(() => {
    /** @type {*} on va chercher le token dans le stockage de session*/
    const userFacebookData = localStorage.getItem("fblst_6447709785249838");
    /**
     * Si il y a un token facebook on déclare une fonction asynchrone 
     * S'il y a un token fb, on effectue une requête à l'api Fk pour avoir les informations (ici mail, nom et photo) de l'utilisateur connecté
     * On stocke ensuite ces données dans une variable d'état "userFacebook" qui les stockent dans un contexte local
     */
    const fetchData = async () => {
      try {
        if (userFacebookData) {
          const response = await fetch(
            `https://graph.facebook.com/me?fields=email,name,picture&access_token=${userFacebookData}`
          );

          if (response.ok) {
            const data = await response.json();
            setUserFacebook(data);
          } else {
            console.error(
              "Erreur lors de la validation du token :",
              response.statusText
            );
          }
        }
      } catch (error) {
        console.error(
          "Une erreur s'est produite lors de la validation du token",
          error
        );
      }
    };
    fetchData();
  }, []);

  async function signin(credentials) {
    const newUser = await login(credentials);
    setUser(newUser);
  }

  async function signout() {
    await logout();
    setUser(null);
  }

  async function signoutGoogle() {
    localStorage.removeItem("userGoogle"); //permet d'enlever le token google
    setUserGoogle({});
  }

  async function signoutFacebook() {
    localStorage.removeItem("fblst_6447709785249838");
    sessionStorage.removeItem("fbssls_6447709785249838");
    setUserFacebook({});
  }

  return (

      <AuthContext.Provider
        value={{
          userGoogle,
          user,
          signin,
          signout,
          signoutGoogle,
          userFacebook,
          signoutFacebook,
        }}
      >
        {children}
      </AuthContext.Provider>

  );
}
