export default function StarRating(starCount) {
  const maxStars = 10;
  const fullStar = (
    <i className="fa-solid fa-star" style={{ color: "#e1ec0d" }}>
      {" "}
    </i>
  );
  const emptyStar = <i className="fa-regular fa-star"></i>;
  const roundedStarCount = Math.round(starCount.starCount * 2);
  const stars = [];
  for (let i = 1; i <= maxStars; i++) {
    if (i <= roundedStarCount) {
      stars.push(fullStar);
    } else {
      stars.push(emptyStar);
    }
  }
  return <>{stars}</>;
}
