import { useEffect, useState } from "react";
import { useLoaderData } from "react-router-dom";
import { signin as login } from "../../apis/auth";
import { signout as logout } from "../../apis/auth";
import { AuthContext } from "../../context";
import { GoogleOAuthProvider } from "@react-oauth/google";
import jwt_decode from "jwt-decode";

export default function AuthProvider({ children }) {
  const initialUser = useLoaderData();
  const [user, setUser] = useState(initialUser);
  const [userGoogle, setUserGoogle] = useState({});
  console.log(userGoogle);
  const [userFacebook, setUserFacebook] = useState({});
  console.log(userFacebook);

  useEffect(() => {
    const userGoogleData = localStorage.getItem("userGoogle");
    if (userGoogleData) {
      const parsedData = JSON.parse(userGoogleData);
      setUserGoogle(parsedData);
    }
  }, []);

  useEffect(() => {
    const userFacebookData = localStorage.getItem("fblst_6447709785249838");
    console.log(userFacebookData);
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://graph.facebook.com/me?fields=email,name,picture&access_token=${userFacebookData}`
        );

        if (response.ok) {
          const data = await response.json();
          setUserFacebook(data);
        } else {
          console.error(
            "Erreur lors de la validation du token :",
            response.statusText
          );
        }
      } catch (error) {
        console.error(
          "Une erreur s'est produite lors de la validation du token :",
          error
        );
      }
    };
    fetchData();
  }, []);

  async function signin(credentials) {
    const newUser = await login(credentials);
    setUser(newUser);
  }

  async function signout() {
    await logout();
    setUser(null);
  }

  async function signoutGoogle() {
    setUserGoogle({});
    localStorage.removeItem("userGoogle");
  }

  async function signoutFacebook() {
    setUserFacebook({});
    localStorage.removeItem("fblst_6447709785249838");
  }

  return (
    <GoogleOAuthProvider clientId="651054691015-uia3b45jud55me9pln2sma2jsovhb223.apps.googleusercontent.com">
      <AuthContext.Provider
        value={{
          userGoogle,
          user,
          signin,
          signout,
          signoutGoogle,
        }}
      >
        {children}
      </AuthContext.Provider>
    </GoogleOAuthProvider>
  );
}
