import React, { useContext } from "react";
import styles from "./Header.module.scss";
import { useState } from "react";
import MobileMenu from "./components/MobileMenu";
import { Link } from "react-router-dom";
import { AuthContext } from "../../context";

export default function Header() {
  const { userGoogle, user, signout} = useContext(AuthContext);


  console.log(userGoogle);

  const [showMenu, setShowMenu] = useState(false);
  const [count, setCount] = useState(0);

  function addCount() {
    setCount(count + 1);
  }
  function handleClick() {
    if (count === 0) {
      setShowMenu(true);
      addCount();
    } else {
      setShowMenu(false);
      setCount(0);
    }
  }

  return (
    <header className={`${styles.header}`}>
      <div className="d-flex justify-content-center">
        <ul className={`${styles.desktopHeader}`}>
          {user ? (
            <ul className="d-flex justify-content-evenly">
              <Link to="/">
                <button className="btn-primary las la-home">
                  <span>Accueil</span>
                </button>
              </Link>
            <Link onClick={() => signout()}>
                <button className="btn-primary-reverse ml10 las la-sign-out-alt">
                  <span className={`${styles.te}`}>Deconnexion</span>
                </button>
              </Link>

            </ul>
          ) : (
            <ul>
              <Link to="/SignUp">
                <button type="button" className={`mr10 btn btn-primary`}>
                  <i className="fas fa-star m5"></i>
                  <span>Inscription</span>
                </button>
              </Link>
              <Link to="/SignIn">
                <button type="button" className={`mr10 btn btn-primary-reverse`}>
                  <i className="fas fa-right-to-bracket m5 m5"></i>
                  <span>Connexion</span>
                </button>
              </Link>
            </ul>
          )}
        </ul>
        <i
          onClick={handleClick}
          className={`las la-bars mr10 positionFixed`}
        ></i>
        {showMenu && (
          <>
            <MobileMenu />
          </>
        )}
      </div>

      <div className="d-flex justify-content-center mt-4">
        <Link to="/">
          <p>LOGO</p>
        </Link>
      </div>
    </header>
  );
}
