import { GoogleMap, Marker, useLoadScript } from "@react-google-maps/api";
import { useEffect, useState } from "react";
import styles from "./MapOfGoogle.module.scss";
import { detailsPlace } from "../../../apis/mapOfGoogle";

const MapOfGoogle = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [places, setPlaces] = useState([]);
  console.log(places);

  const searchNearbyPlaces = async (location) => {
    try {
      const data = await detailsPlace(location);
      //console.log(data);
      setPlaces(data);
    } catch (error) {
      console.error("Error searching for places:", error);
    }
  };

  //fonction permettant de géolocalisé l'utilisateur grâce à son navigateur
  const getUserLocation = () => {
    return new Promise((resolve, reject) => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            resolve({
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            });
          },
          (error) => {
            reject(error);
          }
        );
      } else {
        reject(new Error("Geolocation is not supported by this browser."));
      }
    });
  };

  const { isLoaded } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_API_KEY,
  });

  useEffect(() => {
    if (isLoaded) {
      getUserLocation()
        .then((location) => {
          setUserLocation(location);
          searchNearbyPlaces(location);
        })
        .catch((error) => {
          console.error("Error getting user location:", error);
        });
    }
  }, [isLoaded]);

  /** @type {*image svg pointer} */
  const customMarker = {
    path: "M29.395,0H17.636c-3.117,0-5.643,3.467-5.643,6.584v34.804c0,3.116,2.526,5.644,5.643,5.644h11.759   c3.116,0,5.644-2.527,5.644-5.644V6.584C35.037,3.467,32.511,0,29.395,0z M34.05,14.188v11.665l-2.729,0.351v-4.806L34.05,14.188z    M32.618,10.773c-1.016,3.9-2.219,8.51-2.219,8.51H16.631l-2.222-8.51C14.41,10.773,23.293,7.755,32.618,10.773z M15.741,21.713   v4.492l-2.73-0.349V14.502L15.741,21.713z M13.011,37.938V27.579l2.73,0.343v8.196L13.011,37.938z M14.568,40.882l2.218-3.336   h13.771l2.219,3.336H14.568z M31.321,35.805v-7.872l2.729-0.355v10.048L31.321,35.805",
    fillColor: "red",
    fillOpacity: 2,
    strokeWeight: 1,
    rotation: 0,
    scale: 1,
  };

  return (
    <div className={`${styles.app}`}>
      {!isLoaded ? (
        <h1>Loading...</h1>
      ) : (
        <GoogleMap
          mapContainerClassName={`${styles.mapContainer}`}
          center={userLocation}
          zoom={12}
        >
          {places.map((place) => (
            <div key={place.id}>
              <Marker
                position={{
                  lat: place.geometry.location.lat,
                  lng: place.geometry.location.lng,
                }}
                icon={customMarker}
              />
            </div>
          ))}
        </GoogleMap>
      )}
      {places.map((place, i) => (
        <div key={i} className="d-flex flex-column">
          <div className={`${styles.cart}`}>
            <div className="row">
              <div className="col-5">
                {place.photos && (
                  <>
                    <img
                      src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&maxheight=400&photo_reference=${place.photos[0].photo_reference}&key=${process.env.REACT_APP_GOOGLE_API_KEY}`}
                      alt={`${place.name}`}
                      className=" rounded mx-auto d-block "
                    />
                  </>
                )}
              </div>
              <div className="col-7">
                <div className="d-flex flex-column">
                  <h4 className=" text-center ">{place.name}</h4>
                  <a
                    className={`${styles.decoNone}`}
                    href={place.details.url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <p className="card-text">Adresse : {place.vicinity}</p>
                  </a>
                </div>
              </div>
            </div>
            
            <div className="d-flex flex">
              <div>
                {place.rating && (
                  <p>
                    Note Google : {place.rating}/5 pour{" "}
                    {place.user_ratings_total} votes
                  </p>
                )}
              </div>
              {place.details.opening_hours && (
                <div>
                  <>
                    <h4> Horaire d'ouverture </h4>
                    {place.details.opening_hours.weekday_text.map(
                      (index, i) => (
                        <div key={i}>{index}</div>
                      )
                    )}
                  </>
                </div>
              )}

              <div>
                <a
                  href={`tel:${place.details.formatted_phone_number}`}
                  className={`${styles.decoNone}`}
                >
                  {place.details.formatted_phone_number}
                </a>
              </div>

              <div>
                {place.details.reviews && (
                  <div>
                    <h4>Avis :</h4>
                    {place.details.reviews.map((review, i) => (
                      <div key={i}>
                        <div>{review.author_name} :</div>
                        <div>{review.text}</div>
                        <br></br>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              <div>
                {place.details.website && (
                  <a
                    href={place.details.website}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="btn btn-primary decoNone"
                  >
                    Visiter le site
                  </a>
                )}
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default MapOfGoogle;
